const categories: (Category | 'All')[] = ['All', 'LED Bulbs', 'Smart Lighting', 'Tube Lights', 'Ceiling Lights', 'Spotlights'];

export function ProductFilters({
    activeCategory,
    setActiveCategory,
    searchQuery,
